import { ComponentFixture, TestBed } from '@angular/core/testing';

import { ListprojetComponent } from './listprojet.component';
import {HttpClientTestingModule, HttpTestingController} from "@angular/common/http/testing";
import {HttpClientModule} from "@angular/common/http";

describe('ListprojetComponent', () => {
  let component: ListprojetComponent;
  let fixture: ComponentFixture<ListprojetComponent>;
  let httpTestingController: HttpTestingController;

  beforeEach(async () => {
    await TestBed.configureTestingModule({
      declarations: [ ListprojetComponent ],
      imports: [HttpClientModule, HttpClientTestingModule], // Add HttpClientModule and HttpClientTestingModule

    })
    .compileComponents();
    httpTestingController = TestBed.inject(HttpTestingController);

  });
  afterEach(() => {
    httpTestingController.verify();
  });
  beforeEach(() => {
    fixture = TestBed.createComponent(ListprojetComponent);
    component = fixture.componentInstance;
    fixture.detectChanges();
  });

  it('should create', () => {
    expect(component).toBeTruthy();
  });

  it('should fetch and display a list of projects from the real API', (done: DoneFn) => {
    // Trigger the actual method that fetches data from the API
    component.getProjects();

    // Allow some time for the HTTP request to complete (adjust the timeout as needed)
    setTimeout(() => {
      // Trigger change detection to update the view
      fixture.detectChanges();

      // Access the DOM to make assertions
      const projectElements = fixture.nativeElement.querySelectorAll('[data-test=project-card]');
      expect(projectElements.length).toBeGreaterThan(0);

      done();
    }, 1000); // Adjust the timeout as needed
  });});
